import React from "react";
import * as fcl from "@onflow/fcl";
import App from "next/app";
import Layout from "../components/layout";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { withMobile } from "../shared/enhancers/mobile-enhancer";

// const NODE_ENV = process.env.NODE_ENV;
// const FLOW_CONFIG = process.env.FLOW_CONFIG;
// const { ACCESS_NODE_API, CHALLENGE_HANDSHAKE } = FLOW_CONFIG["production"];
// } = FLOW_CONFIG[NODE_ENV];

toast.configure();

fcl
  .config()
  .put("accessNode.api", "https://access-testnet.onflow.org") // connect to Flow testnet
  .put("challenge.handshake", "https://flow-wallet-testnet.blocto.app/authn"); // use Blocto testnet wallet

class MyApp extends App {
  render(props) {
    const { Component, pageProps } = this.props;
    return (
      <Layout>
        <code>{JSON.stringify(process.env.FLOW_CONFIG.development)}</code>
        <code>{JSON.stringify(process.env.FLOW_CONFIG.production)}</code>
        <Component {...pageProps} />
      </Layout>
    );
  }
}

export default withMobile(MyApp);
