import React from "react";
import PropTypes from "prop-types";
import styled from "@emotion/styled";
import { MOBILE } from "../../../../style/breakpoints";

const Close = styled("div")({
  position: "absolute",
  top: 10,
  right: 10,
  cursor: "pointer",
  zIndex: 1
});

