import React, { useEffect, useContext } from "react";
import styled from "@emotion/styled";

import * as fcl from "@onflow/fcl";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faLink,
  faPlug,
  faWallet,
  faTrophyAlt,
} from "@fortawesome/pro-regular-svg-icons";
import { faLinkDuoTone } from "@fortawesome/pro-duotone-svg-icons";

import { WHITE } from "@style/colors";

import { WalletContext } from "@enhancers/wallet-provider";
import CoralButton from "@library/components/buttons/coral";

import { BASE_TEXT, WEIGHT } from "@style/typography";

import { AuxiliaryPanelHeader } from "@library/components/typography/headers/auxiliary-panel";

import { Divider } from "@library/components/layout";

const Container = styled("div")({
  display: "flex",
  flexDirection: "column",
  borderRadius: 3,
  backgroundColor: WHITE,
  boxShadow: "0 1px 2px 0 rgba(0,0,0,.1)",
  padding: "1rem",
});

const Label = styled("div")({
  ...BASE_TEXT,
  fontSize: 12,
  fontWeight: WEIGHT.BOLD,
});

const Value = styled("div")({
  ...BASE_TEXT,
});

const TextGroup = styled("div")({
  display: "flex",
  flexDirection: "column",
});

const StyledDivider = styled(Divider)({
  marginTop: 5,
  marginBottom: 5,
});

const ctaButtonStyles = {
  height: 24,
  marginTop: 5,
  marginBottom: 5,
};

const StyledCoralButton = styled(CoralButton)({
  ...ctaButtonStyles,
});

const WalletDetails = ({ currentUser }) => {
  const {
    walletConnected,
    walletNotConnected,
    setupFCLAuthHandler,
    walletUser,
    setupUserVault,
    vaultIsSetup,
    setupUserCollection,
    collectionIsSetup,
    walletBalance,
    walletCollection,
    connectActiveWalletToLyraLabs,
  } = useContext(WalletContext);

  useEffect(setupFCLAuthHandler, []);

  const walletIsLinked =
    currentUser && currentUser.walletAddress && currentUser.walletIsSetup;

  const walletIsActive = walletConnected && walletUser;

  const walletHasBalance = walletConnected && vaultIsSetup && walletBalance;

  const walletHasAwards =
    walletConnected && collectionIsSetup && walletCollection;

  return (
    <>
      <AuxiliaryPanelHeader>Flow Wallet</AuxiliaryPanelHeader>
      <Container>
        <TextGroup>
          <Label>
            <FontAwesomeIcon icon={faLink} /> Linked Wallet
          </Label>
          {walletIsLinked ? (
            <Value>{`0x${currentUser.walletAddress}`}</Value>
          ) : walletIsActive ? (
            <StyledCoralButton onClick={connectActiveWalletToLyraLabs}>
              Link Wallet
            </StyledCoralButton>
          ) : (
            <Value>None</Value>
          )}
        </TextGroup>
        <StyledDivider />
        <TextGroup>
          <Label>
            <FontAwesomeIcon icon={faPlug} /> Active Wallet
          </Label>
          {walletIsActive ? (
            <Value>{`0x${walletUser.addr}`}</Value>
          ) : (
            <StyledCoralButton onClick={fcl.authenticate}>
              Connect Wallet
            </StyledCoralButton>
          )}
        </TextGroup>
        <StyledDivider />
        <TextGroup>
          <Label>
            <FontAwesomeIcon icon={faWallet} /> Balance
          </Label>
          {walletHasBalance ? (
            <Value>{`${walletBalance} LYRA`}</Value>
          ) : walletIsActive ? (
            <Value
              onClick={() => {
                console.log("clicked", vaultIsSetup);
              }}
            >
              Click here
            </Value>
          ) : (
            <Value>No active wallet</Value>
          )}
          <Value>{vaultIsSetup}</Value>
        </TextGroup>
        <StyledDivider />
        <TextGroup>
          <Label>
            <FontAwesomeIcon icon={faTrophyAlt} /> Awards
          </Label>
          {walletHasAwards ? (
            <Value>{JSON.stringify(walletCollection)}</Value>
          ) : (
            <Value>No active wallet</Value>
          )}
        </TextGroup>

        {walletConnected &&
          walletUser &&
          walletUser.addr &&
          currentUser &&
          !currentUser.walletIsSetup &&
          !currentUser.walletAddress && (
            <div>
              <CoralButton onClick={connectActiveWalletToLyraLabs}>
                Connect Active Wallet to LyraLabs account
              </CoralButton>
            </div>
          )}
        <div>-------------------------------------</div>
        <div>
          {/* {walletNotConnected && (
            <CoralButton onClick={fcl.authenticate}>Connect Wallet</CoralButton>
          )} */}
          <br />
          <br />
          {walletConnected && !vaultIsSetup && (
            <CoralButton onClick={setupUserVault}>Setup Vault</CoralButton>
          )}
          <br />
          <br />
          {walletConnected && !collectionIsSetup && (
            <CoralButton onClick={setupUserCollection}>
              Setup Collection
            </CoralButton>
          )}

          {/* {walletConnected && (
          <div style={{ fontSize: 14 }}>
            <JSONPretty id="json-pretty" data={walletUser}></JSONPretty>
          </div>
        )} */}
        </div>
      </Container>
    </>
  );
};

export default WalletDetails;
