import React from "react";
import Router from "next/router";

const login = "/";

const checkUserAuthentication = async (req) => {
  console.log("------- incheckUserAuthentication---- ", req);
  const dev = process.env.NODE_ENV !== "production";
  const server = dev ? "http://localhost:3000" : "https://lyralabs.io";
  try {
    console.log("A");
    const response = await fetch(`${server}/api/me`, {
      // headers: {
      //   cookie: req.headers.cookie,
      // },
    });
    console.log("B");
    console.log("response", response);
    const profile = await response.json();
    if (profile.error) {
      return false;
    } else {
      return true;
    }
  } catch (err) {
    console.log("the catch - err", err);
    return false;
  }
};

export const withPrivateRoute = (Component, options = {}) => {
  console.log("XXXXXXXX");
  const WithPrivateRoute = (props) => {
    return <Component {...props} />;
  };
  WithPrivateRoute.getInitialProps = async (ctx) => {
    const { req, res } = ctx;
    console.log("--- pre auth check ctx", ctx);
    const isAuthorized = await checkUserAuthentication(req);
    console.log("isAuthorized", isAuthorized);
    if (!isAuthorized) {
      // Handle server-side and client-side rendering.
      if (res) {
        res?.writeHead(302, {
          Location: login,
        });
        res?.end();
      } else {
        Router.replace(login);
      }
    } else {
      let pageProps = {};
      pageProps = await Component.getInitialProps(ctx);
      return { ...pageProps };
    }
  };
  return WithPrivateRoute;
};
